import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ChatApp() {
  const [text, setText] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const apiUrl = 'http://localhost:3001/api/chat'; // L'URL de votre serveur Node.js
      const requestData = { text };

      const response = await axios.post(apiUrl, requestData);

      setResponse(response.data.message);
    } catch (error) {
      if (error.response) {
        // Gérer les erreurs de réponse HTTP
        console.error('Erreur de réponse HTTP :', error.response.status);
        console.error('Données de réponse :', error.response.data);
      } else if (error.request) {
        // Gérer les erreurs de requête
        console.error('Erreur de requête :', error.request);
      } else {
        // Gérer les erreurs inattendues
        console.error('Erreur inattendue :', error.message);
      }
    }
  };

  useEffect(() => {
    // Lorsque le composant est monté, vous pouvez exécuter la requête initiale ici si nécessaire
    // Exemple : axios.post('http://localhost:3001/api/chat', { text: 'Votre texte ici' })
    //   .then(response => {
    //     const apiResponse = response.data.message;
    //     setResponse(apiResponse);
    //   })
    //   .catch(error => {
    //     console.error('Erreur lors de la requête initiale :', error);
    //   });
  }, []);

  return (
    <div>
      <h1>Bienvenue dans le minichaGpt</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Entrez votre texte"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button type="submit">Envoyer</button>
      </form>
      {response && <div>Réponse : {response}</div>}
    </div>
  );
}

export default ChatApp;
