import React, { useState } from 'react';
import axios from 'axios';


function App() {
  const [text, setText] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const apiUrl = 'http://localhost:3001/api/chat'; // L'URL de notre serveur Node.js
      const requestData = { text };

      const response = await axios.post(apiUrl, requestData);

      setResponse(response.data.message);
    } catch (error) {
      if (error.response) {
        // La requête a été effectuée avec un code de statut différent de 2xx
        console.error('Erreur de réponse HTTP :', error.response.status);
        // Vous pouvez également accéder aux données de réponse en cas d'erreur
        console.error('Données de réponse :', error.response.data);
      } else if (error.request) {
        // La requête n'a pas pu être effectuée
        console.error('Erreur de requête :', error.request);
      } else {
        // Une erreur inattendue s'est produite
        console.error('Erreur inattendue :', error.message);
      }
    }
  };

  return (
    <div>
      <h1>Bienvenue dans le minichaGpt</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Entrez votre texte"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button type="submit">Envoyer</button>
      </form>
      {response && <div>Réponse : {response}</div>}
    </div>
    

  );
  <div id="result"></div>
}

export default App;
